$(function(){$("section#articles a[href^='http://']").attr("target","_blank")});$(window).load(function(){$container=$("section#articles");$container.isotope({itemSelector:"article",layoutMode:"masonry"}).addClass("loaded");$("#filter a").click(function(e){if(e.altKey){$("#filter a").addClass("inactive");$(this).removeClass("inactive")}else{$(this).toggleClass("inactive");$("#filter a:not(.inactive)").size()||$("#filter a").removeClass("inactive")}var t=new Array;$("#filter a:not(.inactive)").each(function(){t[t.length]="."+$(this).parent().attr("class")});$container.isotope({filter:t.join(",")})});$("#more button").click(function(){$.getJSON("/more/"+$("#articles article").size(),function(e){$.each(e,function(e,t){e=moment.unix(e);item=$('<article class="'+t.type+'">'+"<header>"+t.header+"</header>"+t.content+"<footer>"+t.source+"&nbsp;"+'<time datetime="'+e.format()+'">'+e.format("MMM D, YYYY")+"</time>"+"</footer>"+"</article>");$container.append(item).isotope("appended",item)})})})});