$(function(){$("section#articles a[href^='http://']").attr("target","_blank")});$(window).load(function(){limit=25;$container=$("section#articles");$container.isotope({itemSelector:"article",layoutMode:"masonry",columnWidth:$("section#articles article").first().width()}).addClass("loaded");$("#filter a").click(function(e){if(e.altKey){$("#filter a").addClass("inactive");$(this).removeClass("inactive")}else{$(this).toggleClass("inactive");$("#filter a:not(.inactive)").size()||$("#filter a").removeClass("inactive")}var t=new Array;$("#filter a:not(.inactive)").each(function(){t[t.length]="."+$(this).parent().attr("class")});$container.isotope({filter:t.join(",")})});$("#more button").click(function(){$.getJSON("/more/"+$("#articles article").size(),function(e){$.each(e,function(e,t){var e=moment.unix(t.time);$container.append('<article class="'+t.type+' unadded">'+"<header>"+t.header+"</header>"+t.content+"<footer>"+t.source+"&nbsp;"+'<time datetime="'+e.format()+'">'+e.format("MMM D, YYYY")+"</time>"+"</footer>"+"</article>")});$("section#articles a[href^='http://']").attr("target","_blank");$("article.unadded").imagesLoaded(function(){$("article.unadded").each(function(){$container.isotope("appended",$(this));$(this).removeClass("unadded")});$container.isotope({itemSelector:"article",layoutMode:"masonry"})})})})});